import React from 'react';
import axios from 'axios';
import { MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardImage, MDBCardBody, MDBCardTitle, MDBCardText, MDBCardFooter, MDBIcon  } from "mdbreact";
import Header from '../../Accueil';
import Footer from '../Footer/Footer';

class News extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            profil: [],
            nom:"",
            prenom:"",
            email:"",
            telephone:""
        };
        this.handleChange = this.handleChange.bind(this);

    }
    componentDidMount() {    
        axios.get('http://localhost:8080/atelier').then(res => {

            this.setState({ profil: res.data })
            console.log("state" +this.state.profil)

        })
        .catch(function (error) {
          console.log(error);
      })
    }

    handleChange(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }

  render() {
    return (
        <div>
        <div id="fond0">
            <Header />
            <MDBContainer fluid>
            <div className="row" id="chart1">
                <div className="col-md-8">
                <MDBRow>
                    {this.state.profil.length>0 ?(this.state.profil.map(menu=>{
                    return(
                <MDBCol md="12">
                        <MDBCard wide ecommerce>
                            <MDBCardBody cascade  placement="top"
                                    tag="a"
                                    component="i"
                                    componentClass="fa fa-eye grey-text ml-3"
                                    tooltipContent="Quick look"
                                    className="text-center">
                            <MDBCardTitle>
                                <strong id="color">
                                    <h2 id="tex">{menu.titre}</h2>
                                </strong>
                                    <span>Date: { menu.date }</span>
                            </MDBCardTitle>
                            <MDBCardText>
                                <h6>{menu.description}</h6>
                            </MDBCardText>
                            {/* <MDBCardFooter className="px-1">
                                <span className="float-left font-weight-bold">
                                <strong></strong>
                                </span>
                                <span className="float-right">
                                </span>
                            <div className="">
                            </div>
                            </MDBCardFooter> */}

                            </MDBCardBody>
                        </MDBCard>
                        <MDBCardImage
                            cascade
                            src={'http://localhost:8080/atelier/'+ menu.photo}
                            top
                            alt="sample photo"
                            />

                    </MDBCol>
                    )})):""} 
                    </MDBRow>

                </div>
                <div className="col-md-4">
                        <MDBCard className="card-image" style={{
                    backgroundImage: "url('/img/impact02.jpg')"
                    }}>
                <div className="text-white text-center d-flex align-items-center rgba-black-strong py-5 px-4">
                    <div>
                    <h5 className="yellow-text">
                        <MDBIcon icon="" />The world
                    </h5>
                    <MDBCardTitle tag="h3" className="pt-2">
                        <strong>Deforestation</strong>
                    </MDBCardTitle>
                    <p>
                    Deforestation is causing global climate change as well as local change. ... The increase in the carbon content of the atmosphere will therefore lead to an increase in the greenhouse effect, and therefore to global warming.
                    </p>{/* 
                    <MDBBtn color="pink">
                        <MDBIcon icon="clone left" /> View project
                    </MDBBtn> */}
                    </div>
                </div>
                </MDBCard>

                </div>

            </div>
            </MDBContainer>
        <div id="#foot">
            <Footer/>
        </div>

      </div>

      </div>
    );
  }
}

export default News;
