import React from 'react';
import axios from 'axios';
import { MDBRow, MDBCol, MDBCard, MDBCardImage, MDBCardBody, MDBCardTitle, MDBCardText, MDBCardFooter, MDBIcon  } from "mdbreact";


class News extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            profil: [],
            nom:"",
            prenom:"",
            email:"",
            telephone:""
        };
        this.handleChange = this.handleChange.bind(this);

    }
    componentDidMount() {    
        axios.get('https://simplontest04.herokuapp.com/atelier').then(res => {

            this.setState({ profil: res.data })
            console.log("state" +this.state.profil)

        })
        .catch(function (error) {
          console.log(error);
      })
    }

    handleChange(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }

  render() {
    return (
        <div>
            <div className="row">
                <div className="col-md-8">
                        <MDBCard className="card-image" id="fond6">
                <div className="text-white text-center d-flex align-items-center rgba-black-strong py-5 px-4">
                    <div>
                    <h5 className="yellow-text">
                        <MDBIcon icon="" />The world
                    </h5>
                    <MDBCardTitle tag="h3" className="pt-2">
                        <strong>Deforestation</strong>
                    </MDBCardTitle>
                    <p>
                    Deforestation is causing global climate change as well as local change. ... The increase in the carbon content of the atmosphere will therefore lead to an increase in the greenhouse effect, and therefore to global warming.
                    </p>{/* 
                    <MDBBtn color="pink">
                        <MDBIcon icon="clone left" /> View project
                    </MDBBtn> */}
                    </div>
                </div>
                </MDBCard>

                </div>
                <div className="col-md-4">
                <MDBRow>
                    {this.state.profil.length>0 ?(this.state.profil.filter(pro=>pro.visibilite==true).map(menu=>{
                    return(
                <MDBCol>
                        <MDBCard wide ecommerce>
                            <MDBCardImage
                            cascade
                            src={"https://simplontest04.herokuapp.com/public/"+menu.image}
                            top
                            alt="sample photo"
                            />
                            <MDBCardBody cascade  placement="top"
                                    tag="a"
                                    component="i"
                                    componentClass="fa fa-eye grey-text ml-3"
                                    tooltipContent="Quick look"
                                    className="text-center">
                            <MDBCardTitle>
                                <strong id="color">
                                    <h2 id="tex">{menu.titre}</h2>
                                </strong>
                                    <p>Date: { menu.date }</p>
                            </MDBCardTitle>
                            <MDBCardText>
                                <h6>{menu.description}</h6>
                            </MDBCardText>
                            <MDBCardFooter className="px-1">
                                <span className="float-left font-weight-bold">
                                <strong></strong>
                                </span>
                                <span className="float-right">
                                </span>
                            <div className="">
                            </div>
                            </MDBCardFooter>

                            </MDBCardBody>
                        </MDBCard>

                    </MDBCol>
                    )})):""} 
                    </MDBRow>

                </div>

            </div>
      </div>
    );
  }
}

export default News;
