import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";
import jwt_decode from "jwt-decode";
import setAuthToken from "./utils/setAuthToken";
import { MDBContainer, MDBNavbar,MDBNavbarBrand, MDBNavbarNav, MDBNavbarToggler, MDBCollapse } from "mdbreact";

import { setCurrentUser, logoutUser } from "./actions/authActions";
import { Provider } from "react-redux";
import store from "./store";
import Profil from './components/dashboard/Dash/Profil'

import Register from "./components/auth/Register";
import Login from "./components/auth/Login";
import PrivateRoute from "./components/private-route/PrivateRoute";
import Dashboard from "./components/dashboard/SideBar/index";

import Home from './components/layout/component/Home/Home';
import Impact from './components/layout/component/Impact/Impact';
import Risk from './components/layout/component/Risk/Risk';
import News from './components/layout/component/News/News';
import Resolve from './components/layout/component/Resolve/Resolve';
import Deforest from './components/layout/Chart';

import Social from './components/layout/component/Social/Social';
import Footer from './components/layout/component/Footer/Footer';

import "./App.css";
import 'bootstrap/dist/css/bootstrap.min.css';

// Check for token to keep user logged in
if (localStorage.jwtToken) {
  // Set auth token header auth
  const token = localStorage.jwtToken;
  setAuthToken(token);
  // Decode token and get user info and exp
  const decoded = jwt_decode(token);
  // Set user and isAuthenticated
  store.dispatch(setCurrentUser(decoded));
  // Check for expired token
  const currentTime = Date.now() / 1000; // to get in milliseconds
  if (decoded.exp < currentTime) {
    // Logout user
    store.dispatch(logoutUser());

    // Redirect to login
    window.location.href = "./login";
  }
}
class App extends Component {
  state = {
    modal5: false, 
  }

  toggleCollapse = collapseID => () =>
  this.setState(prevState => ({
    collapseID: prevState.collapseID !== collapseID ? collapseID : ""
  }));
  toggle = nr => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber]
    });
  }

  render() {
    return (
      <Provider store={store}>
        <Router>
          <div>
          <div id="fond">
            <MDBContainer fluid>
                <header >
                  <div className="slogo">
                   {/* <p className="logo">Gasy Ecocitizen</p> */}
                    <p className="slogan">Take care of our environment</p>                   
                    <img src="img/logo.png" width="120px"/>
                  </div>
                  <MDBNavbar dark expand="md" style={{ marginTop: "1px" }} id="zetina">
                    <MDBNavbarBrand>
                      <img className=""  src=""  alt = "" height="60px"/>
                    </MDBNavbarBrand>
                    <MDBNavbarToggler onClick={this.toggleCollapse("navbarCollapse3")} />
                      <MDBCollapse id="navbarCollapse3" isOpen={this.state.collapseID} navbar>
                        <MDBNavbarNav left>
                          <li class="nav-item">
                            <Link to="/"><span id="couleur" >Home</span></Link> <span class="sr-only">(current)</span>
                          </li>
                          <li class="nav-item">
                            <Link to="/news"><span id="couleur" >News</span></Link> <span class="sr-only">(current)</span>
                          </li>
                          <li class="nav-item">
                            <Link to="/Impact"><span id="couleur" >Impact</span></Link> <span class="sr-only">(current)</span>
                          </li>
                          <li class="nav-item">
                            <Link to="/Risk"><span id="couleur" >Risk</span></Link> <span class="sr-only">(current)</span>
                          </li>
                          <li class="nav-item">
                            <Link to="/Resolve"><span id="couleur" >Resolve</span></Link> <span class="sr-only">(current)</span>
                          </li>
                          <li class="nav-item">
                            <Link to="/Deforest"><span id="couleur" >Deforestation</span></Link> <span class="sr-only">(current)</span>
                          </li>
                        </MDBNavbarNav>
                        <MDBNavbarNav right>
                          <li class="nav-item" left>
                            <Link to="/login"><span id="couleur" >Sign in</span></Link> <span class="sr-only">(current)</span>
                          </li>
                        </MDBNavbarNav>
                      </MDBCollapse>
                  </MDBNavbar>
                </header>

                <div className="reseau">
                  <Social /> 
                </div>
                <div className="content">
                  <Route exact path="/" exact component={Home} />
                </div>

                <div id="#foot">
                  <Footer /> 
                </div>
              </MDBContainer> 
            </div> 
            <Route exact path="/register" component={Register} />
            <Route exact path="/login" component={Login} />
            <Route path="/profil" component={Profil} />
            <Route path="/" component={Home} />
            <Route path="/Impact" component={Impact} />
            <Route path="/Risk" component={Risk} />
            <Route path="/news" component={News} />
            <Route path="/Resolve" component={Resolve} />
            <Route path="/Deforest" component={Deforest} />
            {/* <Route path="/Dashboard" component={Dashboard} />
            <Route path="/actualite" component={Actualite} />
            <Route path="/List" component={Actualite} />
            <Route path="/Ajout" component={Ajout} />
            <Route path="/Table" component={Table} />
            <Route path="/Admin" exact component={Welcome} /> */}
            <Switch>
              <PrivateRoute exact path="/Admin" component={Dashboard} />
            </Switch>
          </div>
        </Router>
      </Provider>
    );
  }
}
export default App;
