import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import Atelier from './../atelier/atelier';
import Getatelier from './../atelier/getatelier';
import { BrowserRouter as Router } from "react-router-dom";
import { MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavItem, MDBNavLink, MDBNavbarToggler, MDBCollapse } from "mdbreact";


import '@trendmicro/react-sidenav/dist/react-sidenav.css';

class Dashboard extends Component {
  state = {
    modal5: false, 
  }

  get = () =>{
      return   document.getElementById('listecomponent').style.display = 'block'
  }
  //popops login fonction 
  toggleCollapse = collapseID => () =>
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));
  toggle = nr => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber]
    });
  }
  onLogoutClick = e => {
    e.preventDefault();
    this.props.logoutUser();
  };


  render() {

    const { user } = this.props.auth;

    return (
      <div className="container-fluid">
         <div class="container">
            <MDBNavbar color="red" dark expand="md" style={{ marginTop: "1px" }} id="navbar" className="fixed-top" scrolling >
            <MDBNavbarToggler onClick={this.toggleCollapse("navbarCollapse3")} />
                <MDBNavbarBrand>
                    < img  className = ""  src = " img/logocooking.png "  alt = " logo " height="60px"/>
                    <span id="good">GEC</span>
                </MDBNavbarBrand>
                    <MDBCollapse id="navbarCollapse3" isOpen={this.state.collapseID} navbar> 
                    <MDBNavbarNav left>
                    <MDBNavItem> 
                        <MDBNavLink to="/" className="nav-header">Acceuil</MDBNavLink>
                    </MDBNavItem>
                </MDBNavbarNav> 
                <MDBNavbarNav right> 
                <MDBNavItem>
                        <MDBNavLink to="" onClick={this.onLogoutClick} className="nav-header" >Deconnecter</MDBNavLink> 
                </MDBNavItem> 
                    </MDBNavbarNav> 
                    </MDBCollapse> 
               </MDBNavbar>
           </div>
